tinymce.PluginManager.add("example",function(c,d){c.addButton("example",{text:"My button",icon:!1,onclick:function(){c.windowManager.open({title:"Example plugin",body:[{type:"textbox",name:"title",label:"Title"}],onsubmit:function(a){c.insertContent("Title: "+a.data.title)}})}}),c.addMenuItem("example",{text:"Example plugin",context:"tools",onclick:function(){c.windowManager.open({title:"TinyMCE site",url:d+"/dialog.html",width:600,height:400,buttons:[{text:"Insert",onclick:function(){var a=c.windowManager.getWindows()[0];c.insertContent(a.getContentWindow().document.getElementById("content").value),a.close()}},{text:"Close",onclick:"close"}]})}})});